import CartActionTypes from "./cart.types";
import { firestore, getCurrentUser } from "../../firebase/firebase.utils";

export const toggleCartHidden = () => ({
  type: CartActionTypes.TOGGLE_CART_HIDDEN,
});

export const addItem = (item) => ({
  type: CartActionTypes.ADD_ITEM,
  payload: item,
});

export const removeItem = (item) => ({
  type: CartActionTypes.REMOVE_ITEM,
  payload: item,
});

export const clearItemFromCart = (item) => ({
  type: CartActionTypes.CLEAR_ITEM_FROM_CART,
  payload: item,
});

export const clearCart = () => ({
  type: CartActionTypes.CLEAR_CART,
});

export const addCartToFirestoreStart = (cartItems) => ({
  type: CartActionTypes.ADD_CART_TO_FIRESTORE_START,
  payload: cartItems,
});

export const fetchCartFromFirestoreStart = () => ({
  typer: CartActionTypes.FETCH_CART_FROM_FIRESTORE_START,
});

export const fetchCartFromFirestoreSuccess = (cartItems) => ({
  typer: CartActionTypes.FETCH_CART_FROM_FIRESTORE_SUCCESS,
  payload: cartItems,
});

export const fetchCartFromFirestoreFailure = (error) => ({
  typer: CartActionTypes.FETCH_CART_FROM_FIRESTORE_FAILURE,
  payload: error,
});

export const fetchCartItemsFromFirestore = async () => {
  try {
    const userAuth = getCurrentUser();
    const userRef = firestore.doc(`users/${userAuth}/cartItems`);
    console.log(userRef);
  } catch (error) {
    console.log(error);
  }
};
