import { all, put, takeLatest, call } from "redux-saga/effects";

import UserActionTypes from "../user/user.types";

import { auth, googleProvider } from "../../firebase/firebase.utils";

import {
  signInWithGoogleSuccess,
  signInWithGoogleFailure,
} from "./user.actions";

export function* signInWithGoogle() {
  const user = yield auth.signInWithPopup(googleProvider);
  console.log(user);
}

export function* signInWithGoogleStart() {
  yield takeLatest(UserActionTypes.SIGN_IN_WITH_GOOGLE_START, signInWithGoogle);
}

export function* userSaga() {
  yield all([call(signInWithGoogleStart)]);
}
