import { all, put, takeLatest, call } from "redux-saga/effects";

import UserActionTypes from "../user/user.types";
import CartActionTypes from "./cart.types";

import { clearCart } from "./cart.actions";
import {
  createCartProfileDocument,
  getCurrentUser,
} from "../../firebase/firebase.utils";

import { selectCartItems } from "./cart.selectors";

export function* clearCartOnSignOutSuccess() {
  yield put(clearCart());
  yield put(addCartToFirestoreAsync());
}

export function* addCartToFirestoreAsync() {
  try {
    const userAuth = yield put(getCurrentUser());
    yield call(createCartProfileDocument, (userAuth, selectCartItems));
  } catch (error) {
    console.log(
      "there's problem in transporting cart to firestore",
      error.message
    );
  }
}

export function* onSignOutSuccess() {
  yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS, clearCartOnSignOutSuccess);
}

export function* onAddCartToFirestoreStart() {
  yield takeLatest(
    CartActionTypes.ADD_CART_TO_FIRESTORE_START,
    addCartToFirestoreAsync
  );
}

export function* cartSagas() {
  yield all([call(onSignOutSuccess), call(onAddCartToFirestoreStart)]);
}
